mapper.insert:
/* NOTICE_BOARD INSERT */
SELECT 'NOT' || NVL(TRIM(TO_CHAR(SUBSTR(MAX(BOARD_ID),4) + 1,'00000')),'00001') FROM NOTICE_BOARD
INSERT INTO NOTICE_BOARD ( BOARD_ID, MEM_ID, BOARD_TITLE, BOARD_WRITING_DT, BOARD_UPDT_DT, NTBD_AF_ID, BOARD_CNTNT ) VALUES (#{boardId},#{memId},#{boardTitle},SYSDATE,SYSDATE,#{ntbdAfId},#{boardCntnt})

mapper.delete:
/* NOTICE_BOARD DELETE */
DELETE FROM NOTICE_BOARD
WHERE BOARD_ID = #{boardId}
